mapper.select:
SELECT s.ID, s.BASESERV_TYPEID, s.SERV_NAME, s.SERV_MANID, m.SNAME, m.STEL, g.LONGITUDE, g.LATITUDE, t.ERVERSATIS, st.serv_time, s.STATE FROM TBL_SERV s LEFT JOIN tbl_serv_man m ON m.ID = s.SERV_MANID LEFT JOIN tbl_serv_man_gps g ON s.SERV_MANID = g.SERV_MANID LEFT JOIN tbl_satis t ON s.ID = t.SERID LEFT JOIN tbl_serv_time st ON s.SERV_TIMEID=st.ID
and s.SERV_TYPE=#{servType}
and s.STATE=#{states}
and s.BASESERV_TYPEID=#{baseservTypeid}
AND s.SERV_MANID NOT IN ( SELECT d.ID FROM tbl_dis_serv d WHERE d.OPENID = #{openid} )

SELECT COUNT(s.ID) AS ID FROM TBL_SERV s LEFT JOIN tbl_serv_man m ON m.ID = s.SERV_MANID LEFT JOIN tbl_serv_man_gps g ON s.SERV_MANID = g.SERV_MANID LEFT JOIN tbl_satis t ON s.ID = t.SERID
and s.SERV_TYPE=#{servType}
and s.STATE=#{states}
and s.BASESERV_TYPEID=#{baseservTypeid}
AND s.SERV_MANID NOT IN ( SELECT d.ID FROM tbl_dis_serv d WHERE d.OPENID = #{openid} )

mapper.insert:
insert into tbl_serv (ID, SERV_NAME, SERV_TYPE, 
  BASESERV_TYPEID, STATE
  )
values (#{id,jdbcType=BIGINT}, #{servName,jdbcType=VARCHAR}, #{servType,jdbcType=CHAR}, 
  #{baseservTypeid,jdbcType=BIGINT}, #{state,jdbcType=TINYINT}
  )
